   private void combo1(){
       try{
           con=DriverManager.getConnection("jdbc:mysql://localhost:3306/bus","root","");
          String sql="select * from dhaka_boarding_point";
          pst=con.prepareStatement(sql);
         rs=pst.executeQuery();
         while(rs.next())
         {
             String boarding=rs.getString("place");
             point.addItem(boarding);
         }
      }catch(Exception e)
      {
          System.out.println(e);
      }
        
   }
